import { useState, useEffect, Children, cloneElement } from "react";
import "./Carousel.css";

const PAGE_WIDTH = 1024;
const MAX_OVERSCROLL = 30; // Максимальное смещение за пределы границ

export const Carousel = ({ children }) => {
  const [pages, setPages] = useState([]);
  const [offset, setOffset] = useState(0); // Текущее смещение слайдера
  const [isDragging, setIsDragging] = useState(false); // Флаг для отслеживания перетаскивания
  const [startX, setStartX] = useState(0); // Начальная позиция касания/нажатия

  useEffect(() => {
    setPages(
      Children.map(children, (child) => {
        return cloneElement(child, {
          style: {
            height: "100%",
            minWidth: `${PAGE_WIDTH}px`,
            maxWidth: `${PAGE_WIDTH}px`,
          },
        });
      })
    );
  }, [children]);

  // Обработчик начала касания или нажатия мыши
  const handleStart = (clientX) => {
    setIsDragging(true);
    setStartX(clientX);

    // Отключаем transition во время перетаскивания
    const slider = document.querySelector(".all-pages-container");
    if (slider) {
      slider.style.transition = "none";
    }
  };

  // Обработчик перемещения пальца или мыши
  const handleMove = (clientX) => {
    if (!isDragging) return;

    const deltaX = clientX - startX; // Разница между текущей позицией и начальной
    const totalPages = pages.length;
    const maxOffset = -(totalPages - 1) * PAGE_WIDTH;

    let newOffset = offset + deltaX;

    // Ограничение смещения за пределы первого слайда
    if (newOffset > 0) {
      newOffset = Math.min(newOffset, MAX_OVERSCROLL); // Не больше 50px
    }

    // Ограничение смещения за пределы последнего слайда
    if (newOffset < maxOffset) {
      newOffset = Math.max(newOffset, maxOffset - MAX_OVERSCROLL); // Не меньше maxOffset - 50px
    }

    setOffset(newOffset); // Обновляем смещение
    setStartX(clientX); // Обновляем начальную позицию
  };

  // Обработчик завершения касания или отпускания мыши
  const handleEnd = () => {
    if (!isDragging) return;
    setIsDragging(false);

    const totalPages = pages.length;
    const maxOffset = -(totalPages - 1) * PAGE_WIDTH;

    let newOffset = offset;

    // Если вышли за пределы первого слайда, возвращаемся на первый слайд
    if (offset > 0) {
      newOffset = 0;
    }

    // Если вышли за пределы последнего слайда, возвращаемся на последний слайд
    else if (offset < maxOffset) {
      newOffset = maxOffset;
    }

    // Иначе определяем ближайший слайд
    else {
      const pageIndex = Math.round(-offset / PAGE_WIDTH);
      newOffset = -pageIndex * PAGE_WIDTH;
    }

    // Включаем transition для плавной анимации
    const slider = document.querySelector(".all-pages-container");
    if (slider) {
      slider.style.transition = "transform 0.3s ease-in-out";
    }

    setOffset(newOffset); // Устанавливаем финальное смещение
  };

  return (
    <div className="main-container">
      <div className="window">
        <div
          className="all-pages-container"
          style={{ transform: `translateX(${offset}px)` }}
          onTouchStart={(e) => handleStart(e.touches[0].clientX)}
          onTouchMove={(e) => handleMove(e.touches[0].clientX)}
          onTouchEnd={handleEnd}
          onMouseDown={(e) => handleStart(e.clientX)}
          onMouseMove={(e) => handleMove(e.clientX)}
          onMouseUp={handleEnd}
          onMouseLeave={handleEnd}
        >
          {pages.map((page, index) => (
            <div key={index} className="slide">
              {/* Контент слайда */}
            </div>
             ))}
        </div>
      </div>
    </div>
  );
};
